import React, { useState } from "react";
import "./Register.css"; // Assuming you have the CSS file linked correctly

const Register = () => {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [message, setMessage] = useState("");
  const [isSubmitting, setIsSubmitting] = useState(false); // New state for submission loading

  // Validate form
  const validateForm = () => {
    if (!/\S+@\S+\.\S+/.test(email)) {
      setMessage("Please enter a valid email.");
      return false;
    }
    if (password.length < 8) {
      setMessage("Password must be at least 8 characters long.");
      return false;
    }
    return true;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setMessage(""); // Clear any previous messages

    // Validate form before submission
    if (!validateForm()) return;

    setIsSubmitting(true);

    fetch("http://localhost:5000/api/register", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        first_name: firstName,
        last_name: lastName,
        email: email,
        password: password,
      }),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.error) {
          setMessage(data.error);
        } else {
          setMessage("Registration successful!");
          // Clear form fields on success
          setFirstName("");
          setLastName("");
          setEmail("");
          setPassword("");
          setShowPassword(false); // Optionally reset show password
        }
      })
      .catch((error) => {
        console.error("Error:", error);
        setMessage("Registration failed. Please try again.");
      })
      .finally(() => {
        setIsSubmitting(false);
      });
  };

  return (
    <div className="registration-container">
      <h2>Register</h2>
      <form onSubmit={handleSubmit}>
        {/* Floating Label for First Name */}
        <div className="floating-label">
          <input
            type="text"
            value={firstName}
            onChange={(e) => setFirstName(e.target.value)}
            required
            placeholder=" "
            disabled={isSubmitting} // Disable while submitting
          />
          <label htmlFor="first-name">First Name</label>
        </div>

        {/* Floating Label for Last Name */}
        <div className="floating-label">
          <input
            type="text"
            value={lastName}
            onChange={(e) => setLastName(e.target.value)}
            required
            placeholder=" "
            disabled={isSubmitting} // Disable while submitting
          />
          <label htmlFor="last-name">Last Name</label>
        </div>

        {/* Floating Label for Email */}
        <div className="floating-label">
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            placeholder=" "
            disabled={isSubmitting} // Disable while submitting
          />
          <label htmlFor="email">Email</label>
        </div>

        {/* Floating Label for Password */}
        {/* Floating Label for Password */}
        <div className="floating-label">
          <input
            type={showPassword ? "text" : "password"}
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            placeholder=" "
            disabled={isSubmitting}
          />
          <label htmlFor="password">Password</label>
        </div>

        <button type="submit" disabled={isSubmitting} className="ripple-button">
          {isSubmitting ? "Processing..." : "Register"}
        </button>
      </form>
      {message && (
        <div
          className={`notification ${
            message.includes("successful") ? "success" : "error"
          }`}
        >
          {message}
        </div>
      )}
    </div>
  );
};

export default Register;
